import React from 'react';

const textStyle = {
    textAlign: 'right',
    margin: '20px 0', // Adjust the margin to move the text down
    fontSize: '16px', // Adjust the font size
    fontWeight: 'bold', // Adjust the font weight
};

export default function MorePage() {
    return (
        <div>               {/* General page explanation - Text */}
            <p style={textStyle}>
                :לנוחיותכם אספנו מספר מקורות מידע, אתרים וקווים חמים לסיוע והסברה בנושא חרמות
                <span style={{ marginLeft: '5px' }}>:</span> {/* Use marginLeft to adjust the colon position */}
            </p>
            {/* Matzmichim Clickable URL Link - information about how to avoid boycotts */}

            <p style={{ textAlign: 'right' }}>
                <a href="https://matzmichim.org.il/boycott" target="_blank" rel="noopener noreferrer">
                    התמודדות עם חרם חברתי
                </a>
            </p>
            {/* Add additional content below if needed */}
        </div>
    );
}
