import React from 'react';

// Styles for the container that holds the text and ":"
const containerStyle = {
    display: 'flex',
    alignItems: 'flex-start', // Vertically align items to the top
    flexDirection: 'column', // Stack items vertically
};

// Styles for the wrapper that contains the text and ":"
const textWrapperStyle = {
    display: 'flex',
    alignItems: 'center', // Center items horizontally
};

// Styles for the main text
const textStyle = {
    textAlign: 'right',
    fontSize: '16px',
    fontWeight: 'bold',
};

// Styles for the colon (":")
const colonStyle = {
    marginLeft: '5px', // Adjust the colon's position
};

export default function MorePage() {
    return (
        <div style={containerStyle}>
            {/* Text and colon wrapper */}
            <div style={textWrapperStyle}>{/* General page explanation - Text */}
                <p style={textStyle}>
                    לנוחיותכם אספנו מספר מקורות מידע, אתרים וקווים חמים בנושא חרמות:
                </p>
                <span style={colonStyle}>:</span> {/* Colon */}
            </div>

            {/* Matzmichim Clickable URL Link - information about how to avoid boycotts */}
            <p style={{ textAlign: 'right' }}>
                <a href="https://matzmichim.org.il/boycott" target="_blank" rel="noopener noreferrer">
                    התמודדות עם חרם חברתי
                </a>
            </p>
            {/* Add more content here if needed */}
        </div>
    );
}
