import React, { useState } from 'react';
import PhonePopupModal from './PhonePopupModal'; // Import the PhonePopupModal component

function PhonePopup() {
    const [modalOpen, setModalOpen] = useState(false);
    const phoneNumber = '123-456-7890'; // Replace with the actual phone number

    const handleCallClick = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    return (
        <div>
            <p>Click to call: <span onClick={handleCallClick}>123-456-7890</span></p>
            {modalOpen && (
                <PhonePopupModal
                    onClose={handleCloseModal}
                    phoneNumber={phoneNumber}
                />
            )}
        </div>
    );
}

export default PhonePopup;
