import React from 'react';

// Styles for the container that holds the text and email address
const containerStyle = {
    display: 'flex',
    flexDirection: 'column', // Stack items vertically
    alignItems: 'flex-start', // Align all items to the left
    paddingTop: '20px', // Add space at the top
    backgroundColor: '#f7f7f7', // Background color
    padding: '20px', // Add padding to the container
};

// Styles for the main text with right-to-left direction and text alignment
const textStyle = {
    fontSize: '18px', // Increase the font size
    fontWeight: 'bold',
    textAlign: 'right', // Align the text to the right
    direction: 'rtl', // Right-to-left direction for Hebrew text
    float: 'right', // Align text to the right
};


// Styles for the email address
const emailStyle = {
    fontSize: '16px',
    fontWeight: 'bold',
    textAlign: 'left', // Align the email link to the left
    color: '#007bff', // Text color
    textDecoration: 'none', // Remove underlines from the email address
    transition: 'color 0.2s', // Smooth color transition on hover

    // Add a hover effect
    ':hover': {
        color: '#0056b3', // New color on hover
    },
};

export default function ContactUsPage() {
    return (
        <div style={containerStyle}>
            {/* Display the Hebrew text with right-to-left direction and aligned to the right */}
            <p style={textStyle}>
                <span style={{ direction: 'rtl', textAlign: 'right' }}>ניתן ליצור קשר בכתובת המייל:</span>
            </p>

            <p>&nbsp;</p>
            {/* Display the email address with left-to-right direction */}
            <p style={{ textAlign: 'left', direction: 'ltr' }}>
                <a href="mailto:ContantCareNet@gmail.com" style={emailStyle}>
                    ContantCareNet@gmail.com
                </a>
            </p>
        </div>
    );
}
